import React, { Component } from "react";
import ValidationHoc from "../../../../../hoc/validationHoc"
import { reqUpdateUser } from "../../../../../api/api"
import { connect } from "react-redux";
// TODO @SONIA

class DateOfBirth extends React.Component {
    constructor() {
        super();

    }
    componentDidMount() {
        const dateOfBirth = this.props.userData;
        this.props.setInitialData({ dateOfBirth, year: dateOfBirth.split('-')[0], month: dateOfBirth.split('-')[1], date: dateOfBirth.split('-')[2] })
    }
    render() {
        const { year, month, date } = this.props.values;

        return (
            <div>
                <input value={year}
                    className={this.props.errors.dateOfBirth ? "input-area input-area--invalid" : "input-area"}
                    name="year" onChange={this.props.onChange} />
                <input value={month}
                    className={this.props.errors.dateOfBirth ? "input-area input-area--invalid" : "input-area"}
                    name="month" onChange={this.props.onChange} />
                <input
                    className={this.props.errors.dateOfBirth ? "input-area input-area--invalid" : "input-area"}
                    value={date} name="date" onChange={this.props.onChange} />
                <button onClick={async (e) => {
                    await this.props.onChange(e);
                    this.props.checkValidationErrors(e);
                    if (await this.props.okToSubmit()) {

                        const result = await reqUpdateUser(this.props.user.email, this.props.values);
                        if (result.status === 0) {
                            alert(`succeed to add bank detail! ${JSON.stringify(result.data)}`);
                        }
                    }
                }
                }
                    name="dateOfBirth"
                    value={`${year}-${month}-${date}`}
                    className="green-button"
                >Save Birthday</button>
                <div className="error-message">{this.props.errors.dateOfBirth}</div>
            </div>
        );
    }
}
export default connect(state => ({ user: state.user }), {})(
    ValidationHoc(DateOfBirth));
