import React, { Component } from 'react';
export default function InputComponent(props) {
    const { onChange, values, errors, name, title, placeholder, type } = props;
    return (<div>
        <label htmlFor={title}>{title}</label>
        <input
            onChange={onChange}
            value={values[name] || ''}
            className={errors[name] && "input-area--invalid"}
            placeholder={placeholder}
            name={name}
            id={title}
            type={type}
            autoComplete="off"
        />
        <div className="error-message">{errors[name]}</div>
    </div>);
}
