export class MapBox extends Component {
  constructor(props){
      super(props);
      this.state = {
        markers:[false,false,false],
        showingInfoWindow: false,  //Hides or the shows the infoWindow
        activeMarker: {},          //Shows the active marker upon click
        selectedPlace: {} ,         //Shows the infoWindow to the selected place upon a marker
        changed:false,
        zoomValue:''
    };
  }
  componentDidMount(){
      if(this.props.distance){//calculate zoom 
        let l=document.querySelector('.mapbox').clientWidth;//px
        let dipx=window.screen.deviceXDPI;
        l=l*25.4/dipx/1000//m
        let scale=this.props.distance*1000/l;
        if(scale>591657550.5){
            this.setState({zoomValue:1});
            return;
        }
        if(scale>295828775.3){
            this.setState({zoomValue:2});
            return;
        }
        if(scale>147914387.6){
            this.setState({zoomValue:3});
            return;
        }
        if(scale>73957193.82){
            this.setState({zoomValue:4});
            return;
        }
        if(scale>36978596.91){
            this.setState({zoomValue:5});
            return;
        }
        if(scale>18489298.45){
            this.setState({zoomValue:6});
            return;
        }
        if(scale>9244649.227){
            this.setState({zoomValue:7});
            return;
        }
        if(scale>4622324.614){
            this.setState({zoomValue:8});
            return;
        }
        if(scale>2311162.307){
            this.setState({zoomValue:9});
            return;
        }
        if(scale>1155581.153){
            this.setState({zoomValue:10});
            return;
        }
        if(scale>577790.5767){
            this.setState({zoomValue:11});
            return;
        }
        if(scale>288895.2884){
            this.setState({zoomValue:12});
            return;
        }
        if(scale>144447.6442){
            this.setState({zoomValue:13});
            return;
        }
        if(scale>72223.8220){
            this.setState({zoomValue:14});
            return;
        }
        if(scale>36111.9110){
            this.setState({zoomValue:15});
            return;
        }
        if(scale>18055.95552){
            this.setState({zoomValue:16});
            return;
        }
        if(scale>9027.977761){
            this.setState({zoomValue:17});
            return;
        }
        if(scale>4513.988880){
            this.setState({zoomValue:18});
            return;
        }
        if(scale>2256.994440){
            this.setState({zoomValue:19});
            return;
        }
        if(scale>1128.497220){
            this.setState({zoomValue:20});
            return;
        }


      }


  }
  componentDidUpdate(prevProps, prevState){
    // console.log(prevState,';;;',prevState)
    if(prevProps.locations!==this.props.locations){
        console.log("changed")
        this.forceUpdate();
    }
  }
  onMarkerClick = (props, marker, e) =>
  this.setState({
    selectedPlace: props,
    activeMarker: marker,
    showingInfoWindow: true
  });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  render() {
    // const infoStyle = Object.assign({}, infoStyles.info);
    //const {location}=this.props;

    console.log(this.props.locations)

    return (
      <div className="mapbox">
        <CurrentLocation

          centerAroundCurrentLocation
          google={this.props.google}
          {...this.props}

        >
          {/* <Marker  address={this.props.locations} name={'current location'} /> */}
          {this.props.locations===''?<Marker  name={'current location'} />:<Marker  address={this.props.locations} name={'current location'} />}

          <Marker
              //place_id={this.props.locations}
              position={{ lat: -33.918551, lng:  151.219999 }}
              onClick={this.onMarkerClick}
              name={'Prenuptial agreement'} 
              time={'Due in 1 day'}
              poster={'Posted by Piyush S. 13 mins ago'}
              price={'$80'}
              profilephoto={'https://pbs.twimg.com/profile_images/1036730403514736650/PCRxFiEt_400x400.jpg'}
              icon={markerIcon}
          />
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >

            <div  className="mapbox--infoWindow">
              <div className="mapbox--infoWindow--top">
                <div className="mapbox--infoWindow--top__img">
                  <img src={this.state.selectedPlace.profilephoto}/>
                </div>
                <div className="mapbox--infoWindow--top__earn">
                <span>{this.state.selectedPlace.price}</span>
                </div>
              </div>
              <div className="mapbox--infoWindow--bottom">
                <h4>{this.state.selectedPlace.name}</h4>
                <p>{this.state.selectedPlace.time}</p>
                <span>{this.state.selectedPlace.poster}</span>
                <button>View Task</button>
              </div>
            </div>
          </InfoWindow>

          <Marker
              position={{ lat: -33.900128, lng:  151.224562 }}
              onClick={this.onMarkerClick}
              name={'Wall mount 65 inch TV'} 
              price={'$50'}
              time={'Due in 6 hours'}
              poster={'post by Purushi,21 hours ago'}
              profilephoto={'https://pbs.twimg.com/media/Dyg-PPzXcAAY5Wv?format=jpg&name=small'}
              icon={markerIcon}
          />
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >

            <div  className="mapbox--infoWindow">
              <div className="mapbox--infoWindow--top">
                <div className="mapbox--infoWindow--top__img">
                  <img src={this.state.selectedPlace.profilephoto}/>
                </div>
                <div className="mapbox--infoWindow--top__earn">
                <span>{this.state.selectedPlace.price}</span>
                </div>
              </div>
              <div className="mapbox--infoWindow--bottom">
                <h4>{this.state.selectedPlace.name}</h4>
                <p>{this.state.selectedPlace.time}</p>
                <span>{this.state.selectedPlace.poster}</span>
                <button>View Task</button>
              </div>
            </div>
          </InfoWindow>
          <Marker
              className="mapbox__maker"
              //address={[{long_name:"Kensington",short_name:"Kensington"},{long_name:"Randwick City Council",short_name:"Randwick"},{long_name:"New South Wales",short_name:"NSW"},{long_name:"Australia",short_name:"AU"},{long_name:"2033",short_name:"2033"}]}
              position={{ lat: -33.912605, lng:  151.235999 }}
              onClick={this.onMarkerClick}
              name={'Tree looper with tree mulcher'} 
              price={'$140'}
              time={'Due 12 hours ago'}
              poster={'post by papa H,21 hours ago'}
              profilephoto={'https://pbs.twimg.com/media/Dyg6-4xWoAEUNut?format=jpg&name=small'}
              icon={markerIcon}
          />
          {/* //-33.900128, 151.224562 */}
          <InfoWindow

            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
            <div  className="mapbox--infoWindow">
              <div className="mapbox--infoWindow--top">
                <div className="mapbox--infoWindow--top__img">
                  <img src={this.state.selectedPlace.profilephoto}/>
                </div>
                <div className="mapbox--infoWindow--top__earn"><p>EARN</p><span>{this.state.selectedPlace.price}</span></div>
              </div>
              <div className="mapbox--infoWindow--bottom">
                <h4>{this.state.selectedPlace.name}</h4>
                <p>{this.state.selectedPlace.time}</p>
                <span>{this.state.selectedPlace.poster}</span>
                <button>View Task</button>
                </div>
            </div>
          </InfoWindow>
        </CurrentLocation>


      </div>
    );
  }
}

