import React, { Component } from "react";
import { reqTaskList } from "../../api/api";
class Slider extends Component{
    constructor(props){
        super(props);
    }
    async componentDidMount() {
        try {
          const result = await reqTaskList();
          if (result.status === 0) {
            this.setState({ data: result.data });
          }
        } catch (error) {
          //SONIA：handle这的error，不然react会报错
          alert("failed to request task list");
        }
      }
    render(){
        return(
            <>
                <div className="homepage__scrolling-items-slider">
                    {this.state.data.map((item, index) =>(<div></div>))}
                    <img src="https://longinsurance.biz/wp-content/uploads/2014/09/lis-header.jpg"></img>
                </div>
                {/* ?<div><img src="https://photography.roytanck.com/wp-content/uploads/2017/07/DSCF3539-2500x1000.jpg"></img></div> */}
            </>
        )
    }
}
export default Slider;
