import React from "react";
import "./side-bar.scss";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faSortDown } from "@fortawesome/free-solid-svg-icons";
// TODO @SONIA

class SideBar extends React.Component {
  constructor() {
    super();
    this.state = {
      menuToggle: false
    }
  }

  componentDidUpdate(prevProps, prevState) {
    if (JSON.stringify(this.props) !== JSON.stringify(prevProps)) {
      this.setState({ menuToggle: false })
    }
  }

  render() {
    const { taskData, userRole } = this.props;
    const { price, posterEmail, status, offers } = taskData;
    return (
      <div className="side-bar">
        <div className="side-bar__payment">
          <div className="payment__title">TASK BUDGET</div>
          <div className="payment__price">
            <h2>$</h2>
            <h2>{price}</h2>
          </div>
          {userRole !== "poster"
            ?
            (
              (status === "assigned" && userRole === "assignee" && < div className="green-button ">Request Payment</div>) ||
              (status === "assigned" && <div className="green-button button--disabled">Assigned</div>) ||
              (status === "completed" && userRole === "assignee" && <div className="green-button">Leave Review</div>) ||
              (status === "completed" && <div className="green-button button--disabled">Completed</div>) ||
              (status === "reviewed" && <div className="green-button button--disabled">Reviewed</div>) ||
              (status === "open" && (offers && offers.find(offer => offer.email === this.props.user.email))
                && <>
                  <div>You offered ${offers.find(offer => offer.email === this.props.user.email).price}</div>
                  <div className="green-button" onClick={() => {
                    this.props.history.push(
                      `${this.props.match.url}/update-offer/${this.props.match.params[0]}-${this.props.match.params.id}`
                    );
                  }}
                  >Update your offer</div>
                </>) ||
              (status === "open" && <div className="green-button" onClick={() => {
                //如果没登陆的话自动跳转登陆
                if (!this.props.user.email) {
                  this.props.history.push(`${this.props.match.url}/login`);
                } else {
                  this.props.history.push(
                    `${this.props.match.url}/make-an-offer/${this.props.match.params[0]}-${this.props.match.params.id}`
                  );
                }
              }}
              >
                Make an offer
              </div>)
            )
            :
            ((status === "open" &&
              <div className="green-button" onClick={() => {
                this.props.history.push(
                  `${this.props.match.url}/review-offers/${this.props.match.params[0]}-${this.props.match.params.id}`
                );
              }}
              >Review offers</div>) ||
              (status === "assigned" &&
                <div className="green-button" onClick={() => {
                  this.props.history.push(
                    `${this.props.match.url}/release-payment/${this.props.match.params[0]}-${this.props.match.params.id}`
                  );
                }}
                >Release Payment</div>)
              ||
              (status === "completed" &&
                <div className="green-button" onClick={() => {
                  this.props.history.push(
                    `${this.props.match.url}/leave-a-review/${this.props.match.params[0]}-${this.props.match.params.id}`
                  );
                }}
                >Leave Review</div>)
              ||
              (status === "reviewed" &&
                <div className="green-button button--disabled">Reviewed</div>)
            )
          }
        </div>


        <div className="more-options__header" onClick={() => { this.setState({ menuToggle: !this.state.menuToggle }) }}>
          <span >
            More Options
              </span>
          <FontAwesomeIcon icon={faSortDown} />

        </div>
        {this.state.menuToggle && (
          <div className="more-options__dropdown-menu">

            {userRole === "poster" && <div className="option" onClick={() => {
              //TODO: 如果没登录的话先post/edit然后最后提示登录，登录成功后再跳转
              if (!this.props.user.email) {
                this.props.history.push(`${this.props.match.url}/login`);
              } else {
                this.props.history.push(
                  `${this.props.match.url}/post-similar/${this.props.match.params[0]}-${this.props.match.params.id}`
                );
              }
            }}>Edit Task</div>}

            <div className="option" onClick={() => {
              //TODO: 如果没登录的话先post/edit然后最后提示登录，登录成功后再跳转
              if (!this.props.user.email) {
                this.props.history.push(`${this.props.match.url}/login`);
              } else {
                this.props.history.push(
                  `${this.props.match.url}/edit/${this.props.match.params[0]}-${this.props.match.params.id}`
                );
              }
            }}>Post a Similar Task</div>

            <div className="option">Set up Alerts</div>

            {taskData.status === "open" && userRole === "poster" &&
              <div className="option">Cancel Task</div>}
          </div>
        )
        }
      </div >

    );
  }
}
export default connect(state => ({ user: state.user }), {})(
  withRouter(SideBar)
);
