import React, { Component } from "react";
import { GoogleMap, Map, GoogleApiWrapper, InfoWindow, Marker  } from 'google-maps-react';
import CurrentLocation from './map';
import markerIcon  from "../../../../assets/markerIcon.png";
import "./map-box.scss";
// TODO @DAVIN
const mapStyles = {
  map: {

    width: '80%',
    height: '100%'
  }
};
// const infoStyles = {
//   info:{
//     // margin:0 auto,
//     border:'none',
//     background:'#33717f',
//   }
// }

export class MapBox extends Component {

  state = {
      markers:[false,false,false],
      showingInfoWindow: false,  //Hides or the shows the infoWindow
      activeMarker: {},          //Shows the active marker upon click
      selectedPlace: {}          //Shows the infoWindow to the selected place upon a marker
  };

  onMarkerClick = (props, marker, e) =>
  this.setState({
    selectedPlace: props,
    activeMarker: marker,
    showingInfoWindow: true
  });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  render() {
    // const infoStyle = Object.assign({}, infoStyles.info);
    return (
      <div className="mapbox">
        <CurrentLocation
          centerAroundCurrentLocation
          google={this.props.google}
        >

          <Marker onClick={this.onMarkerClick} name={'current location'} />
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
            <div>
              <h4>{this.state.selectedPlace.name}</h4>
            </div>
          </InfoWindow>
          <Marker
              position={{ lat: -33.918551, lng:  151.219999 }}
              onClick={this.onMarkerClick}
              name={['tasker1']} 
              icon={markerIcon}
          />
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
            <div  className="mapbox--infoWindow">
              <div className="mapbox--infoWindow__img">
                <img src="https://img.icons8.com/doodle/48/000000/girl.png"/>
              </div>
              <div className="mapbox--infoWindow__earn"></div>
              <h4>{this.state.selectedPlace.name}</h4>
            </div>
          </InfoWindow>
          <Marker
              className="mapbox__maker"
              position={{ lat: -33.912605, lng:  151.235999 }}
              onClick={this.onMarkerClick}
              name={'tasker2'} 
              icon={markerIcon}
          />
          <InfoWindow

            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
            <div  className="mapbox--infoWindow">
              <div className="mapbox--infoWindow__img">
                <img src="https://img.icons8.com/doodle/48/000000/girl.png"/>
              </div>
              <div className="mapbox--infoWindow__earn"></div>
              <h4>{this.state.selectedPlace.name}</h4>
              <p>Due 12 hours ago</p>
              <span>post by papa H,21 hours ago</span>
              <button>View Task</button>
            </div>
          </InfoWindow>
        </CurrentLocation>


      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyDy7fG8VcN_upR7UZ7lC63h7cVbVwYu558'
})(MapBox);
