const inititalState = {
    location: ''
}

const location = (state = inititalState, action) => {
    console.log('======', action)
    switch (action.type) {
      case 'CHANGE_LOCATION':
        return {
          ...state,
          distance: action.location.location
        }
      default:
        return state;
    }
}

export default location;
