import React, { Component } from "react";
import "./task-menu.scss";


// TODO @JUBI
class TaskMenu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dropDownBox:false,
      distanceChoicePage: false,
      priceChoicePage: false,
      taskTypeChoicePage: false,
      drag: false,
      //data: []
    };
    this.distanceChoice = this.distanceChoice.bind(this);
    this.priceChoice = this.priceChoice.bind(this);
    this.taskTypeChoice = this.taskTypeChoice.bind(this);
    this.distanceSetP1 = this.distanceSetP1.bind(this);

    this.priceSetP1 = this.priceSetP1.bind(this);
    this.priceSetP2 = this.priceSetP2.bind(this);
    // this.distanceSetP2 = this.distanceSetP2.bind(this);

    this.inPerson = this.inPerson.bind(this);
    this.remotely = this.remotely.bind(this);
    this.trunOnAssOnly = this.trunOnAssOnly.bind(this);
    this.placeAutoComplete = this.placeAutoComplete.bind(this);
  }
  componentDidMount () {
    const script = document.createElement("script")

    script.src = "//maps.googleapis.com/maps/api/js?key=AIzaSyCkUOdZ5y7hMm0yrcCQoCvLwzdM6M8s5qk&libraries=places&callback=initMa"
    script.async = true

    document.body.appendChild(script)
  }
  placeAutoComplete(){
    const input = document.querySelector("location--typehead--search");
    const autocomplete = new google.maps.places.Autocomplete(input);
    autocomplete.addListener('place_changed', function() {
      infowindow.close();
      marker.setVisible(false);
      var place = autocomplete.getPlace();
      if (!place.geometry) {
        // User entered the name of a Place that was not suggested and
        // pressed the Enter key, or the Place Details request failed.
        window.alert("No details available for input: '" + place.name + "'");
        return;
      }

      // If the place has a geometry, then present it on a map.
      if (place.geometry.viewport) {
        map.fitBounds(place.geometry.viewport);
      } else {
        map.setCenter(place.geometry.location);
        map.setZoom(17);  // Why 17? Because it looks good.
      }
      marker.setPosition(place.geometry.location);
      marker.setVisible(true);

      var address = '';
      if (place.address_components) {
        address = [
          (place.address_components[0] && place.address_components[0].short_name || ''),
          (place.address_components[1] && place.address_components[1].short_name || ''),
          (place.address_components[2] && place.address_components[2].short_name || '')
        ].join(' ');
      }

      infowindowContent.children['place-icon'].src = place.icon;
      infowindowContent.children['place-name'].textContent = place.name;
      infowindowContent.children['place-address'].textContent = address;
      infowindow.open(map, marker);
    });

  }
