import React, { Component } from "react";
import "./toggle-textbox.scss";
import TextEditor from "../text-editor/text-editor";
import { Route, withRouter, Link } from "react-router-dom";
import { connect } from "react-redux";
import { login } from "../../redux/actions";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faChevronDown } from "@fortawesome/free-solid-svg-icons";
import { get_time_diff } from "../../utils/time-difference"
// TODO @SONIA

class ToggleTextbox extends Component {
  constructor(props) {
    super(props);
    this.state = { message: {}, detail: "", toggle: true, overflow: false };
    this.messageRef = React.createRef();
  }
  componentDidMount() {
    this.messageRef.current.style.maxHeight = "120px";
    this.setState(this.props);
  }
  componentDidUpdate(prevProps, prevState) {
    // console.log(this.props.detail)
    // console.log(this.messageRef,this.messageRef.current.scrollHeight,this.messageRef.current.style.maxHeight.substr(0, this.messageRef.current.style.maxHeight.length - 2) )
    if (JSON.stringify(prevProps) !== JSON.stringify(this.props)) {
      this.setState(this.props);
      this.messageRef.current.style.maxHeight = "120px"; //一定要加上这一行，不然maxheight是之前mount的元素的？
      this.setState({ overflow: false, toggle: true });
      // console.log(this.props.detail)
    }
    if (JSON.stringify(prevState) !== JSON.stringify(this.state)) {
      if (Number(this.messageRef.current.scrollHeight) > Number(this.messageRef.current.style.maxHeight.substr(0, this.messageRef.current.style.maxHeight.length - 2))) {
        this.setState({ overflow: true })
      }
    }
  }
  render() {
    const { message, toggle, detail, overflow } = this.state;

    return (<div>
      <div className="toggle-textbox">
        <div ref={this.messageRef} className={(toggle && overflow) ? 'toggle-textbox__message blur' : 'toggle-textbox__message'} >

          {Object.keys(message).length !== 0 ?
            <>
              <div className="poster-detail__item">
                <div className="avatar-image">

                  <img src={message.photo} alt="" />
                </div>
                <div className="poster-detail__item--detail">
                  <div className="user-name-holder">
                    <a href="" className="user-name">
                      {message.username}
                    </a>
                  </div>
                  <div className="bold small-title">{message.rating}  rating</div>
                  <div className="bold small-title"> {message.completionRate} Completion rate</div>

                </div>
              </div>
              <div className="toggle-textbox__message--content">{message.messageContent}</div>
            </> :
            detail}

          {(!toggle || overflow) ?

            <button
              onClick={() => {
                if (toggle) {
                  if (Number(this.messageRef.current.scrollHeight) > Number(this.messageRef.current.style.maxHeight.substr(0, this.messageRef.current.style.maxHeight.length - 2))) {
                    this.messageRef.current.style.maxHeight = this.messageRef.current.scrollHeight + 'px';
                  }

                } else {
                  this.messageRef.current.style.maxHeight = "120px";
                }
                this.setState({ toggle: !toggle });
              }}

            >
              {toggle ? 'More' : 'Less'} <FontAwesomeIcon icon={faChevronDown} className="arrow-icon" style={{ transform: toggle ? '' : 'rotate(180deg)' }} />
            </button> : ''}

        </div>


      </div>
      {/* <div style={{position: 'relative', top: (!toggle || overflow)  ? '20px' : ''}}> */}
      <div style={{ marginTop: (!toggle || overflow) ? '22px' : '' }}>

        {Object.keys(message).length !== 0 ?
          <div className="toggle-textbox__message--bottom"><span>{get_time_diff(message.postDate)} ago</span>
            {this.props.user.email && <Link>Reply</Link>}
          </div>
          : ''}</div>
    </div>
    );
  }
}
export default connect(state => ({ user: state.user }), { login })(
  withRouter(ToggleTextbox)
);

