import React, { Component } from "react";
import { Link } from "react-router-dom";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { logoutUser } from "../../redux/actions";
import "./nav-bar.scss";
// TODO @SONIA

class NavBar extends Component {
  constructor() {
    super();
    this.state = {
      sticky: false
    };
  }

  componentDidMount() {
    window.addEventListener("scroll", () => {
      if (window.pageYOffset > 0) {
        this.setState({ sticky: true });
      } else {
        this.setState({ sticky: false });
      }
    });
  }


  render() {
    let { pathname } = this.props.history.location;
    return (
      <header className={`nav-bar ${(this.state.sticky || pathname !== "/home") && 'sticky'}`} >
        <div className="nav-bar__container">
          <ul>
            <li>
              <div className="logo">
                <Link to="/home">Airtasker</Link>
              </div>
            </li>
            <li>
              <div className="nav-bar__button--pink">
                <a onClick={() => { this.props.history.push(`${pathname}/post`); }}>Post a Task</a>
              </div>
            </li>
            <li>
              <Link to="/browse-tasks">Browse Tasks</Link>
            </li>
          </ul>
          <ul style={{ display: this.props.user.email ? "none" : "" }}>
            <li>
              <div className="nav-bar__button--transparent"
                onClick={() => { this.props.history.push(`${pathname}/signup`); }}>
                Sign Up
              </div>
            </li>
            <li>
              <div className="nav-bar__button--transparent"
                onClick={() => { this.props.history.push(`${pathname}/login`); }}>
                Log In
              </div>
            </li>
          </ul>

          <ul style={{ display: this.props.user.email ? "" : "none" }}>
            <div className="nav-bar__user">
              <li>
                <Link to="/my-tasks">My Tasks</Link>
              </li>
              <li>
                <Link to="/account/dashboard">Account</Link>
              </li>
              <li>
                <Link to="/home" onClick={this.props.logoutUser}>Log Out</Link>
              </li>
            </div>
          </ul>
        </div>
      </header >
    );
  }
}

export default connect(state => ({ user: state.user }), { logoutUser })(
  withRouter(NavBar)
);
